INSERT INTO `product` (`id_product`,`name`,`name_tag_date_released`,`description`,`url_picture`,`date_released`) VALUES (6,'La Ligne Verte','la-ligne-verte-2000','Paul Edgecomb, Gardien-chef du pénitencier de Cold Mountain en 1935, était chargé de veiller au bon déroulement des exécutions capitales. Parmi les prisonniers se trouvait un colosse du nom de John Coffey...','La-Ligne-Verte.jpg','2000-03-01');
